mapper.select:
SELECT count(*) FROM fina_paymethod fp LEFT JOIN fina_paymethod_store fps ON fps.paymethod_code = fp.method_code LEFT JOIN store s ON s.`code` = fps.store_code
1=1
AND s.`code`=#{storeCode}

SELECT s.`name` storeName, fp.id, fp.method_name, fp.method_code, fp.method_type, fp.method_status, fp.if_custome, fp.cost_rate, fp.min_costfee, fp.max_costfee, fp.create_time, fp.creator, fp.ablestage_template_code FROM fina_paymethod fp LEFT JOIN fina_paymethod_store fps ON fps.paymethod_code = fp.method_code LEFT JOIN store s ON s.`code` = fps.store_code
1=1
AND s.`code`=#{storeCode}
ORDER BY
${order.property} ${order.direction}
limit #{offset}, #{pageSize}

SELECT fp.id, fp.method_name, fp.method_code, fp.method_type, fp.method_status, fp.if_custome, fp.cost_rate, fp.min_costfee, fp.max_costfee, fp.create_time, fp.creator, fp.ablestage_template_code, fp.remark FROM fina_paymethod fp LEFT JOIN fina_paymethod_store fps ON fps.paymethod_code = fp.method_code
method_status = 'ENABLE' AND fps.store_code=#{storeCode}
AND ablestage_template_code LIKE #{stageCode}
