mapper.select:
select DISTINCT
cucs.*,cus.customer_level,cus.name AS customer_name,cus.mobile AS customer_mobile,smi.meal_name AS meal_name
from customer_contract AS cucs


    order by

        ${order.property} ${order.direction}


limit #{offset}, #{pageSize}

mapper.sql:
LEFT JOIN customer cus ON (cucs.customer_id = cus.id)
LEFT JOIN sm_meal_info smi ON (cucs.meal_id = smi.id)
LEFT JOIN sm_contract_operate_time_summary scos ON (cucs.contract_code = scos.contract_code)


        ( cucs.contract_code like CONCAT('%', #{keyword}, '%')
        OR cus.name like CONCAT('%', #{keyword}, '%')
        OR cus.mobile like CONCAT('%', #{keyword}, '%') )


        AND cucs.contract_status = #{contractStatus}


        AND cucs.designer = #{designer}


        AND cucs.auditor = #{auditor}


        AND cucs.designer_code = #{designerCode}


        AND cucs.auditor_org_code = #{auditorOrgCode}




                AND cucs.create_time >= #{startDate}


                AND cucs.create_time <= #{endDate}




                AND scos.material_submit_audit_time >= #{startDate}


                AND scos.material_submit_audit_time <= #{endDate}




                AND scos.material_assign_audit_time >= #{startDate}


                AND scos.material_assign_audit_time <= #{endDate}




                AND scos.material_audit_pass_time >= #{startDate}


                AND scos.material_audit_pass_time <= #{endDate}




                AND scos.material_audit_refuse_time >= #{startDate}


                AND scos.material_audit_refuse_time <= #{endDate}




                AND scos.material_sign_time >= #{startDate}


                AND scos.material_sign_time <= #{endDate}



    AND cucs.store_code = #{storeCode}
    AND cucs.current_change_version is NULL
    AND cucs.contract_status IS NOT NULL
    AND cucs.order_flow_status  <>  'APPLY_REFUND'
