mapper.select:
SELECT material.*,dosage.id project_material_id, dosage.domain_name, dosage.budget_dosage, dosage.loss_factor, dosage.loss_dosage, dosage.store_sale_price dosage_store_sale_price, dosage.dosage_pricing_mode, dosage.project_proportion, dosage.original_dosage original_dosage_single, dosage.convert_unit, dosage.dosage_remark FROM sm_project_change_material material LEFT JOIN sm_sku_change_dosage dosage ON material.id = dosage.project_material_id
contract_code = #{contractCode}
AND category_code = #{categoryCode}
AND category_detail_code = #{categoryDetailCode}
AND product_category_url LIKE CONCAT(#{productCategoryUrl}, '%')

select pcm.* from sm_project_change_material pcm
where pcm.product_category_url LIKE CONCAT(#{changeCategoryUrl},'%')
AND pcm.contract_code = #{contractCode}
