mapper.select:
SELECT material.*,dosage.id project_material_id, dosage.domain_name, dosage.budget_dosage, dosage.loss_factor, dosage.loss_dosage, dosage.no_loss_dosage, dosage.store_sale_price dosage_store_sale_price, dosage.dosage_pricing_mode, dosage.project_proportion, dosage.dosage_remark, dosage.convert_unit, psup.`name` as supplier_name ,psup.`code` as supplier_code FROM sm_project_material material LEFT JOIN sm_sku_dosage dosage ON material.id = dosage.project_material_id LEFT JOIN prod_sku psku ON psku.`code` = material.sku_code LEFT JOIN prod_supplier psup ON psup.id = psku.supplier_id
contract_code = #{contractCode}
AND category_code = #{categoryCode}
AND category_detail_code = #{categoryDetailCode}

mapper.insert:
insert into sm_project_material ( id, product_category_url, contract_code, category_code, category_detail_code, sku_code,
design_remark, audit_remark, sku_sqec, sku_model, product_name, sku_image_path, store_sale_price,
quota_describe, check_scale, attribute1, attribute2, attribute3, create_user,
create_time, update_user, update_time,material_unit, brand, brand_id) VALUES

    (#{item.id},#{item.productCategoryUrl},#{item.contractCode},#{item.categoryCode}
    ,#{item.categoryDetailCode},#{item.skuCode},#{item.designRemark},#{item.auditRemark},#{item.skuSqec}
    ,#{item.skuModel},#{item.productName}
    ,#{item.skuImagePath},#{item.storeSalePrice},#{item.quotaDescribe},#{item.checkScale},#{item.attribute1}
    ,#{item.attribute2},#{item.attribute3},#{item.createUser},#{item.createTime},#{item.updateUser},#{item.updateTime}
    ,#{item.materialUnit},#{item.brand}, #{item.brandId})
