mapper.select:
SELECT spd.id, spd.change_log_id, spd.change_no, spd.contract_code, spd.create_time, spd.current_status, spd.change_category_name, spd.change_category_url, spd.material_remarks, spd.design_director_remarks, cc.designer, cc.house_addr, cc.address_province, cc.address_city, cc.address_area, cc.designer_mobile, cc.valuate_area, c.name, c.mobile
ORDER BY
${order.property} ${order.direction}
limit #{offset},#{pageSize}

mapper.sql:
FROM
    sm_change_detail spd
LEFT JOIN
 customer_contract  cc
on spd.contract_code=cc.contract_code
LEFT JOIN
 customer c
ON cc.customer_id=c.id

    cc.store_code=#{storeCode}


            AND  spd.change_category_url IS NULL


            AND  spd.change_category_url=#{catalogUrl}



        AND  (spd.change_no LIKE #{keyword} OR  spd.contract_code LIKE #{keyword})


        AND  spd.pass_materials_department=#{passFlag}


        AND  cc.auditor_org_code=#{orgCode}


        AND spd.current_status IN

            #{item}

mapper.select:
select scd.id, scd.change_log_id, scd.contract_code, scd.change_no, scd.change_category_url, c.name, c.mobile, cc.designer, scd.change_category_name, scd.current_status, scd.download_status, scd.download_times, scd.create_time FROM sm_change_detail as scd LEFT JOIN customer_contract as cc on cc.contract_code = scd.contract_code LEFT JOIN customer as c on c.id = cc.customer_id
(scd.contract_code = #{keyword} OR c.name LIKE CONCAT("%", #{keyword}, "%"))
AND scd.current_status = #{currentStatus}
AND scd.create_user = #{createUser}
AND cc.auditor_org_code=#{orgCode}
AND cc.store_code = #{storeCode}
ORDER BY
${order.property} ${order.direction}
limit #{offset},#{pageSize}

select count(1) from sm_change_detail detail
    LEFT JOIN customer_contract as contract on contract.contract_code = detail.contract_code
    LEFT JOIN customer as customer on customer.id = contract.customer_id


        (detail.contract_code = #{keyword}
        OR customer.name LIKE CONCAT("%", #{keyword}, "%"))


        AND detail.current_status = #{currentStatus}


        AND  detail.create_user = #{createUser}


        AND  contract.auditor = #{auditor}


        AND  contract.store_code = #{storeCode}

select * from sm_change_detail as scd
LEFT JOIN sm_change_log as scl on scl.id = scd.change_log_id


         scd.contract_code = #{contractCode}


         AND scl.change_version_no = #{changeVersionNo}

select count(*)
 from sm_change_detail as scd
    LEFT JOIN customer_contract as cc on cc.contract_code = scd.contract_code
    LEFT JOIN customer as c on c.id = cc.customer_id


        (c.`name` LIKE CONCAT('%',#{keyword},'%') or scd.contract_code = #{keyword}  or c.mobile = #{keyword})


        AND DATE(scd.create_time) >= DATE(#{startDate})


        AND DATE(scd.create_time) <= DATE(#{endDate})



            AND scd.print_count = #{printCount}


            AND scd.print_count >= #{printCount}


    AND cc.store_code = #{storeCode}

select scd.id, scd.contract_code, scd.change_no, c.`name`, c.mobile, cc.second_contact, cc.second_contact_mobile, scd.create_time, fpm.change_amount, scd.print_count from sm_change_detail as scd LEFT JOIN customer_contract as cc on cc.contract_code = scd.contract_code LEFT JOIN customer as c on c.id = cc.customer_id LEFT JOIN fina_projectchange_money as fpm on fpm.change_no = scd.change_no
(c.`name` LIKE CONCAT('%',#{keyword},'%') or scd.contract_code = #{keyword} or c.mobile = #{keyword})
AND DATE(scd.create_time) >= DATE(#{startDate})
AND DATE(scd.create_time) <= DATE(#{endDate})
AND scd.print_count = #{printCount}
AND scd.print_count >= #{printCount}
AND cc.store_code = #{storeCode}
ORDER BY scd.print_count ASC, id DESC limit #{offset},#{pageSize}

mapper.update:
UPDATE sm_change_detail SET print_count = print_count + 1
WHERE id IN

    #{id}
